<script>
import axios from 'axios'
import pagination from "~/components/dashboard/Pagination";
import Modal from "~/components/dashboard/Modal";

export default {
    middleware: ['auth','verify','admin'],
    layout: 'dashboard',
    data: () => ({
      users: {},
      user: null,
      modal: {
        status: false,
        delete: false,
      }
    }),
  components: {
      pagination,
      Modal
  },
  methods: {
    fetchUsers(page = this.$route.query.page || 1) {
      axios
      .get('/verify?page=' + page)
      .then(response => (this.users = response.data))
    },
    update(id, status) {
      axios.patch('/verify', {
        id: id,
        status: status
      })
      .then(res => {
        this.fetchUsers()
      })
    },
    openStatusModal(index){
      this.modal.status = !this.modal.status
      this.user = index
    },
    async updateStatus() {
      const { data } = await axios.patch('/verify', {id:this.users.data[this.user].id, status: 'configuration'})
      this.users.data[this.user].step = 'configuration'
    }
  },
  mounted() {
    this.fetchUsers()
  },
  beforeDestroy() {
    clearInterval(this.intervalId)
  },
  watch: {
    users: function (val) {
      this.$router.replace({ name: "dashboard.movies", query: {page: val.current_page} })

    },
  }
}

</script>
